INSERT INTO laravel.external_commands (id, created_at, updated_at, deleted_at, command, description, pre, platform) VALUES (132, '2023-11-29 21:34:05.818', '2023-11-29 21:34:05.818', null, 'mkfile', 'Create a file.', 'Syntax
